use Psr\Http\Message\ServerRequestInterface;
use TYPO3\CMS\Backend\Routing\UriBuilder;
use TYPO3\CMS\Core\Http\HtmlResponse;
use TYPO3\CMS\Core\Utility\GeneralUtility;

class MyRouteController
{
    public function mainAction(ServerRequestInterface $request): ResponseInterface
    {
        $uriBuilder = GeneralUtility::makeInstance(UriBuilder::class);

        // Create a link to a backend module
        $uri = $uriBuilder->buildUriFromRoute('web_layout', ['id' => 42]);

        // Create a link to a custom module controller action
        $uriWithAction = $uriBuilder->buildUriFromRoute('web_ExtkeyExample.MyModuleController_list');

        // Build URI based on current request, but with modified parameters
        $uriFromRequest = $uriBuilder->buildUriFromRequest($request, ['action' => 'edit', 'id' => 42]);

        // Create a link based on a defined route path (not identifier)  
        $uriFromPath = $uriBuilder->buildUriFromRoutePath('/module/web/layout');

        return new HtmlResponse('<p>This is an example response</p>');
    }
}

