final class AvailableActionsForExtensionEvent
{
    public function __construct(
        private readonly string $extensionKey,
        private array $actions
    ) {
    }

    public function getExtensionKey(): string
    {
        return $this->extensionKey;
    }

    public function getActions(): array
    {
        return $this->actions;
    }

    public function setActions(array $actions): void
    {
        $this->actions = $actions;
    }

    public function addAction(string $identifier, array $action): void
    {
        $this->actions[$identifier] = $action;
    }

    public function removeAction(string $identifier): void
    {
        unset($this->actions[$identifier]);
    }
}
