enum State: int
{
    case STATE_NEW = 1;
    case STATE_PENDING = 2;
    case STATE_DECLINED = 3;
    case STATE_ACCEPTED = 4;

    /**
     * Get the state label
     */
    public function getStateLabel(): string
    {
        return match ($this) {
            self::STATE_NEW => 'new',
            self::STATE_PENDING => 'pending',
            self::STATE_DECLINED => 'declined',
            self::STATE_ACCEPTED => 'accepted',
        };
