<?php
declare(strict_types=1);

namespace MyVendor\MyExtension\Backend\EventListener;

use TYPO3\CMS\Backend\Search\Event\BeforeSearchInDatabaseRecordProviderEvent;

final class MyEventListener
{
    public function __invoke(BeforeSearchInDatabaseRecordProviderEvent $event): void
    {
        // Exclude the tx_myextension_domain_model_mymodel table from searches
        $event->addDeniedTable('tx_myextension_domain_model_mymodel');

        // Search only on pages with the given IDs (1, 2, and 42)
        $event->setSearchPids([1, 2, 42]);
    }
}

