<?php

declare(strict_types=1);

namespace MyVendor\MyExtension\Extbase\EventListener;

use TYPO3\CMS\Extbase\Event\Configuration\BeforeFlexFormConfigurationOverrideEvent;

final class MyEventListener
{
    public function __invoke(BeforeFlexFormConfigurationOverrideEvent $event): void
    {
        $originalFlexFormConfiguration = $event->getOriginalConfiguration();
        $frameworkConfiguration = $event->getFrameworkConfiguration();
        $flexFormConfiguration = $event->getConfiguration();

        // Implement your custom override process here
        $modifiedFlexFormConfiguration = $this->processConfiguration(
            $originalFlexFormConfiguration,
            $frameworkConfiguration,
            $flexFormConfiguration
        );

        $event->setConfiguration($modifiedFlexFormConfiguration);
    }

    private function processConfiguration(array $original, array $framework, array $flexForm): array
    {
        // Custom override logic
        return $flexForm;
    }
}
