define(['TYPO3/CMS/Recordlist/LinkBrowser'], function(LinkBrowser) {
  'use strict';

  /**
   * GitHub handler for the TYPO3 link browser
   */
  class GitHubLinkHandler {

    /**
     * Defines the repository number directly, and the link
     *
     * @param {Event} event
     */
    handleEvent(event) {
      event.preventDefault();

      const value = document.getElementById('gihublink_url').value.trim();
      let url = null;
      if (value.length > 0) {
         url = 'github:' + value;
      }

