// Register JavaScript files
$assetCollector->addJavaScript(
    string $identifier,          // used as array key, should be unique
    string $source,              // file or path to JavaScript, or inline code
    array $attributes = [],      // additional HTML attributes for the <script> tag
    array $options = []
    // options:
    // - priority (bool) = false           // render in <head> section if true
    // - useNonce (bool) = false          // add the nonce attribute when CSP is enabled
    // - external (bool) = false          // if set, no URI processing is done to the source
    // - excludeFromConcatenation (bool)  // Excludes the script from concatenation (page.includeJSFooter/page.includeJS)
    // - disableCompression (bool)        // Disables compression of the script (page.includeJSFooter/page.includeJS)
);

// Check if a JavaScript file is registered
$assetCollector->hasJavaScript(string $identifier): bool;

// Register inline JavaScript
$assetCollector->addInlineJavaScript(
    string $identifier,          // used as array key, should be unique
    string $source,              // JavaScript code
    array $attributes = [],      // additional HTML attributes for the <script> tag
    array $options = []
    // options:
    // - priority (bool) = false  // render in <head> section if true
    // - useNonce (bool) = false  // add the nonce attribute when CSP is enabled
);

// Check if inline JavaScript is registered
$assetCollector->hasInlineJavaScript(string $identifier): bool;

// Register StyleSheet files
$assetCollector->addStyleSheet(
    string $identifier,          // used as array key, should be unique
    string $source,              // file or path to CSS file
    array $attributes = [],      // additional HTML attributes for the <link> tag
    array $options = []
    // options:
    // - priority (bool) = false          // control the order of the stylesheet
    // - useNonce (bool) = false         // add the nonce attribute when CSP is enabled
    // - external (bool) = false          // if set, no URI processing is done to the source
    // - excludeFromConcatenation (bool) // Excludes the CSS file from concatenation (page.includeCSSLibs/page.includeCSS)
    // - disableCompression (bool)       // Disables compression of the CSS file (page.includeCSSLibs/page.includeCSS)
    // - forceOnTop (bool)               // Adds a CSS file before any other CSS file (page.includeCSSLibs/page.includeCSS)
    // - allWrap                         // Wraps the generated <link> tag
);

// Check if a CSS file is registered
$assetCollector->hasStyleSheet(string $identifier): bool;

// Register inline CSS
$assetCollector->addInlineStyleSheet(
    string $identifier,          // used as array key, should be unique
    string $source,              // CSS code
    array $attributes = [],      // additional HTML attributes for the <style> tag
    array $options = []
    // options:
    // - priority (bool) = false  // control the order of the stylesheet
    // - useNonce (bool) = false  // add the nonce attribute when CSP is enabled
);

// Check if inline CSS is registered
$assetCollector->hasInlineStyleSheet(string $identifier): bool;

// Register media like icons or images to be available on the page
$assetCollector->addMedia(
    string $identifier,          // used as array key, should be unique
    string $source,              // path to the media file
    array $additionalInformation = []
);

// Check if media is registered
$assetCollector->hasMedia(string $identifier): bool;

// Get all registered JavaScript assets
$assetCollector->getJavaScripts(?bool $priority = null): array;

// Get all registered inline JavaScript assets
$assetCollector->getInlineJavaScripts(?bool $priority = null): array;

// Get all registered StyleSheet assets
$assetCollector->getStyleSheets(?bool $priority = null): array;

// Get all registered inline StyleSheet assets
$assetCollector->getInlineStyleSheets(?bool $priority = null): array;

// Get all registered media assets (images, icons, etc.)
$assetCollector->getMedia(): array;

```

