<?php

declare(strict_types=1);

namespace MyVendor\MyExtension\Resource\EventListener;

use TYPO3\CMS\Core\Resource\Event\AfterDefaultUploadFolderWasResolvedEvent;
use TYPO3\CMS\Core\Resource\Folder;

final class MyEventListener
{
    public function __invoke(AfterDefaultUploadFolderWasResolvedEvent $event): void
    {
        // Get the original folder
        $originalFolder = $event->getFolder();

        // Get context information
        $table = $event->getTable();
        $pid = $event->getPageId();

        // Check if we want to handle this case
        if ($table === 'pages' && $pid === 123) {
            // Create a new folder/retrieve a different one
            $myFolder = $this->createFolder();

            // Set the new folder
            $event->setFolder($myFolder);
        }
    }

    private function createFolder(): Folder
    {
