namespace TYPO3\CMS\Core\Attribute;

/**
 * This is a dummy class for documentation purposes.
 * In TYPO3 v13+, \TYPO3\CMS\Core\Attribute\AsEventListener is available
 */
#[
    \Attribute(\Attribute::TARGET_CLASS | \Attribute::IS_REPEATABLE)
]
final class AsEventListener
{
    /**
     * @param string $event The event to listen to.
     * @param string|null $method The method to call when the event is triggered.
     *                           If null, the __invoke method will be called.
     * @param int $priority The priority of the listener. Higher numbers mean earlier execution.
     */
    public function __construct(
        public string $event,
        public ?string $method = null,
        public int $priority = 0
    )
    {
    }
}

