public function findRightPaddedValues(): array
{
    $queryBuilder = $this->connectionPool->getQueryBuilderForTable('my_table');
    $queryBuilder
        ->select('uid')
        ->addSelect(
            $queryBuilder->expr()->rightPad(
                'some_field',
                10,
                ' '
            ),
            'padded_value'
        )
        ->from('my_table');

    return $queryBuilder->executeQuery()->fetchAllAssociative();
}
