    ":sk1": "$mallstore#leaseenddate_2020-04-01#storeid_",
    ":sk2": "$mallstore#leaseenddate_2020-07-01#storeid_"
  },
  "KeyConditionExpression": "#pk = :pk and #sk1 BETWEEN :sk1 AND :sk2",
