import * as React from "react";
import { useStyletron } from "baseui";
import { Card } from "baseui/card";

export default function Media({ left, right }) {
  const [css] = useStyletron();
  return (
    <Card>
      <div
        className={css({
          display: "flex",
          fontSize: "14px",
          lineHeight: "1.25",
        })}
      >
        <div
          className={css({
            padding: "8px",
          })}
        >
          {left}
        </div>
        <div
          className={css({
            padding: "8px",
            borderLeft: "solid 2px #eee",
          })}
        >
          {right}
        </div>
      </div>
    </Card>
  );
```

