    value: 'Initial Value',
    // Disable the editor if initial value is not yet ready
    // enabled: !!value,
  });

  return (
    <div>
      <Plate editor={editor}>
        <PlateContent />
      </Plate>

      <button
        onClick={() => {
          // Replace with HTML string
          editor.tf.setValue('Replaced Value');

          // Replace with JSON value
          editor.tf.setValue([
            {
              type: 'p',
              children: [{ text: 'Replaced Value' }],
            },
          ]);

          // Replace with empty value
          editor.tf.setValue();
        }}
      >
        Replace Value
      </button>

      <button
        onClick={() => {
          editor.tf.reset();
        }}
      >
        Reset Editor
      </button>
    </div>
  );
}
