TITLE: Sending OTP without Ratelimiting in Next.js
DESCRIPTION: This snippet demonstrates an insecure implementation of sending a One-Time Password (OTP) via email using a Next.js server action. It generates a random 6-digit code and sends it to the user's email.
SOURCE: https://github.com/unkeyed/unkey/blob/main/apps/www/content/blog/ratelimiting-otp.mdx#2025-04-10_snippet_0

LANGUAGE: typescript
CODE:
```
"use server";
import { randomInt } from "crypto";

export async function sendOTP(formData: FormData) {
  try {
    const email = formData.get("email") as string | null;
    if (!email) {
      return {
        success: false,
        error: "Email was not supplied, please try again",
        statusCode: 400,
      };
    }
    const otp = randomInt(100000, 999999).toString();

    const { data, error } = await emails.send({
      from: "james@unkey.com",
      to: email,
      subject: "OTP code",
      text: `Your OTP code is ${otp}`
    });
    // handled error
    if (error) {
      console.error(error);
      return { success: false, error: "Failed to send email", statusCode: 500 };
    }
    return {
      success: true,
      statusCode: 201,
    };
    //catch
  } catch (e) {
    return { success: false, error: "Failed to send email", statusCode: 500 };
  }
}
