// runtime.tsx

import React from 'react';

export function runtimeStyledDiv(className) {
  return function Component(
    props: React.ComponentProps<'div'>
  ) {
    return (
      <div
        {...props}
        className={[props.className, className]
          .filter(Boolean)
          .join(' ')}
      />
    );
  };
}
```

