that.submitOrder = function() {
      if (!orderReady()) {
        return;
      }
      var orderItems = ko.toJS(that.items);
      var orderMsg = {
        sessionID: that.sessionID(),
        action: "save",
        collection: "orders",
        document: {
          username: that.username(),
          items: orderItems
        }
      }
      eb.send('vertx.mongopersistor', orderMsg, function(reply) {
