MathJax.Hub.Config({
    config: ["MMLorHTML.js"],
    jax: ["input/TeX", "output/HTML-CSS", "output/NativeMML"],
    extensions: ["tex2jax.js", "MathMenu.js", "MathZoom.js"],
    tex2jax: {
        inlineMath: [ ["\\(","\\)"] ],
        displayMath: [ ["\\[","\\]"] ]
    },
    displayAlign: 'left',
    showProcessingMessages: false,
    messageStyle: 'none'
});
</script>
