import {Deck, LayersList} from '@deck.gl/core';
import {ScatterplotLayer, TextLayer} from '@deck.gl/layers';

type Flight = {
  callSign: string;
  lon: number;
  lat: number;
  alt: number;
};

const deckInstance = new Deck({
  initialViewState: {
    longitude: -122.4,
    latitude: 37.8,
