  data: {
    // this is required so that the layer knows how many points to draw
    length: data.pointCount,
    attributes: {
      getPosition: {value: data.positions, size: 3},
      getColor: {value: data.colors, size: 3},
    }
  },
  // constant accessor works without raw data
  getNormal: [0, 0, 1]
});
