```
import React from 'react';
import {DeckGL} from '@deck.gl/react';
import {H3HexagonLayer} from '@deck.gl/geo-layers';
import type {PickingInfo} from '@deck.gl/core';

type DataType = {
  hex: string;
  count: number;
