import React from 'react';
import {DeckGL} from '@deck.gl/react';
import {GridLayer, GridLayerPickingInfo} from '@deck.gl/aggregation-layers';

type BikeRack = {
  ADDRESS: string;
  SPACES: number;
  COORDINATES: [longitude: number, latitude: number];
