import {Deck, Widget} from '@deck.gl/core';

class LoadingIndicator extends Widget {
  element?: HTMLDivElement;
  size: number;

  constructor(options: {
    size: number;
  }) {
    // Note: In modern JS/TS, `super(options)` or `super({id: 'loading-indicator', ...options})`
    // is usually called here to pass props to the base class. The example lacks this.
    // Assuming 'id' and 'props' are managed internally by the base class or context.
    this.size = options.size;
    // TODO - initialize base class properly if needed based on actual Deck.gl Widget implementation
  }

  onRenderHTML(el: HTMLElement) {
    el.className = 'spinner';
    el.style.width = `${this.size}px`;
    el.style.height = `${this.size}px`; // Added height for square spinner often needed
    // TODO - create animation for .spinner in the CSS stylesheet
  }

  onRedraw({layers}) {
    // Check if ANY layer is currently NOT loaded
    const isVisible = layers.some(layer => !layer.isLoaded);
    // Access the root DOM element managed by the widget system
    // The base Widget class is expected to provide a rootElement property
    // This might differ slightly in the actual Deck.gl API
    if (this.rootElement) {
      this.rootElement.style.display = isVisible ? 'block' : 'none';
    }
  }
}

// Example of adding the custom widget to a Deck instance
