TITLE: Building a UI Component
DESCRIPTION: This React component provides a user interface for the AI calculator. It takes an expression as input, calls the `calculateExpression` Server Action, and displays the result.  It uses the `useState` hook for managing the result, loading state, and expression input.
SOURCE: https://github.com/voltagent/voltagent/blob/main/website/docs/integrations/nextjs.md#_snippet_7

LANGUAGE: typescript
CODE:
```
"use client";

import { useState } from "react";
import { calculateExpression } from "../actions";

export function Calculator() {
  const [result, setResult] = useState<string | null>(null);
  const [loading, setLoading] = useState(false);
  const [expression, setExpression] = useState("");

  async function handleSubmit(formData: FormData) {
    const expr = formData.get("expression") as string;
    if (!expr.trim()) return;

    setLoading(true);
    try {
      const calcResult = await calculateExpression(expr);
      setResult(calcResult);
      setExpression(expr);
    } catch (error) {
      console.error("Calculation error:", error);
      setResult("Error calculating expression");
    } finally {
      setLoading(false);
    }
  }

  return (
    <div>
      <h2>AI Calculator</h2>
      <form action={handleSubmit}>
        <label htmlFor="expression">Enter calculation:</label>
        <input
          id="expression"
          name="expression"
          type="text"
          placeholder="E.g. (5 + 3) * 2"
          required
        />
        <button type="submit" disabled={loading}>
          {loading ? "Calculating..." : "Calculate"}
        </button>
      </form>

      {result && (
        <div>
          <h3>Result:</h3>
          <p>{expression} = {result}</p>
        </div>
      )}
    </div>
  );
}
```

