  const y = useY()

  // âŒ This is not possible because y also tries to read x.name
  y.name

  function doSomething() {
    // âœ… Read y properties in computed or actions
    const yName = y.name
    // ...
  }

  return {
    name: ref('I am X'),
  }
})

const useY = defineStore('y', () => {
  const x = useX()

  // âŒ This is not possible because x also tries to read y.name
  x.name

  function doSomething() {
    // âœ… Read x properties in computed or actions
