TITLE: WebSocket Server Implementation - TypeScript
DESCRIPTION: Type-safe server-side WebSocket implementation with Socket.IO, including interface definitions for events and payloads.
SOURCE: https://github.com/wasp-lang/wasp/blob/main/web/versioned_docs/version-0.14.0/advanced/web-sockets.md#2025-04-22_snippet_2

LANGUAGE: typescript
CODE:
```
import { v4 as uuidv4 } from 'uuid'
import { getFirstProviderUserId } from 'wasp/auth'
import { type WebSocketDefinition, type WaspSocketData } from 'wasp/server/webSocket'

export const webSocketFn: WebSocketFn = (io, context) => {
  io.on('connection', (socket) => {
    const username = getFirstProviderUserId(socket.data.user) ?? 'Unknown'
    console.log('a user connected: ', username)

    socket.on('chatMessage', async (msg) => {
      console.log('message: ', msg)
      io.emit('chatMessage', { id: uuidv4(), username, text: msg })
    })
  })
}

type WebSocketFn = WebSocketDefinition<
  ClientToServerEvents,
  ServerToClientEvents,
  InterServerEvents,
  SocketData
>

interface ServerToClientEvents {
  chatMessage: (msg: { id: string, username: string, text: string }) => void;
