import {
  updateTask,
  createTask,
  getTasks,
  useQuery,
} from 'wasp/client/operations'

const TaskView = ({ task }) => {
  const handleIsDoneChange = async (event) => {
    try {
      await updateTask({
        id: task.id,
        isDone: event.target.checked,
      })
    } catch (error) {
      window.alert('Error while updating task: ' + error.message)
