TITLE: Creating Tasks with User Context in TypeScript
DESCRIPTION: TypeScript version of task creation with proper type definitions and user context handling.
SOURCE: https://github.com/wasp-lang/wasp/blob/main/web/versioned_docs/version-0.15.0/auth/overview.md#2025-04-22_snippet_6

LANGUAGE: typescript
CODE:
```
import { type Task } from 'wasp/entities'
import { type CreateTask } from 'wasp/server/operations'
import { HttpError } from 'wasp/server'

type CreateTaskPayload = Pick<Task, 'description'>

export const createTask: CreateTask<CreateTaskPayload, Task> = async (
  args,
  context
) => {
  if (!context.user) {
    throw new HttpError(403)
  }

  const Task = context.entities.Task
  return Task.create({
    data: {
      description: args.description,
      user: {
        connect: { id: context.user.id },
      },
    },
  })
}
