TITLE: TypeScript Database Seeding Function
DESCRIPTION: TypeScript version of the database seeding function with proper type annotations and interfaces.
SOURCE: https://github.com/wasp-lang/wasp/blob/main/web/versioned_docs/version-0.16.0/data-model/databases.md#2025-04-22_snippet_4

LANGUAGE: typescript
CODE:
```
import { createTask } from './actions.js'
import { type DbSeedFn } from 'wasp/server'
import { sanitizeAndSerializeProviderData } from 'wasp/server/auth'
import { type AuthUser } from 'wasp/auth'
import { PrismaClient } from '@prisma/client'

export const devSeedSimple: DbSeedFn = async (prisma) => {
  const user = await createUser(prisma, {
    username: 'RiuTheDog',
    password: 'bark1234',
  })

  await createTask(
    { description: 'Chase the cat', isDone: false },
    { user, entities: { Task: prisma.task } }
  )
};

async function createUser(
  prisma: PrismaClient,
  data: { username: string, password: string }
): Promise<AuthUser> {
  const newUser = await prisma.user.create({
    data: {
      auth: {
        create: {
          identities: {
            create: {
              providerName: 'username',
              providerUserId: data.username,
              providerData: await sanitizeAndSerializeProviderData<'username'>({
                hashedPassword: data.password
              }),
            },
          },
        },
      },
    },
  })

  return newUser
}
```

