TITLE: Implementing Password Reset Component in React/TypeScript
DESCRIPTION: This component handles password reset by submitting a new password along with a token received via URL. It manages error states, handles form input, and navigates to the home page upon successful reset.
SOURCE: https://github.com/wasp-lang/wasp/blob/main/web/docs/auth/email/create-your-own-ui.md#2025-04-22_snippet_3

LANGUAGE: tsx
CODE:
```
export function PasswordReset() {
  const [error, setError] = useState<Error | null>(null)
  const [newPassword, setNewPassword] = useState('')
  const navigate = useNavigate()

  async function handleSubmit(event: React.FormEvent<HTMLFormElement>) {
    event.preventDefault()
    setError(null)
    try {
      // The token is passed as a query parameter
      const token = new URLSearchParams(window.location.search).get('token')
      if (!token) throw new Error('Token not found in URL')
      await resetPassword({ token, password: newPassword })
      navigate('/')
    } catch (error: unknown) {
      console.error('Error during password reset:', error)
      setError(error as Error)
    }
  }

  return (
    <form onSubmit={handleSubmit}>
      {error && <p>Error: {error.message}</p>}

      <input
        type="password"
        autoComplete="new-password"
        value={newPassword}
        onChange={(e) => setNewPassword(e.target.value)}
        placeholder="New password"
      />

      <button type="submit">Reset password</button>
    </form>
  )
}
```

