SOURCE: https://github.com/web-infra-dev/rspack/blob/main/packages/rspack-test-tools/tests/hotCases/runtime/import-after-download/__snapshots__/web/1.snap.txt#2025-04-21_snippet_2

LANGUAGE: javascript
CODE:
```
"use strict";
self["webpackHotUpdate"]('main', {
"./file.js": 
/*!*****************!*\
  !*** ./file.js ***!
  \*****************/
(function (__unused_webpack_module, __webpack_exports__, __webpack_require__) {
__webpack_require__.r(__webpack_exports__);
__webpack_require__.d(__webpack_exports__, {
  "default": () => (__WEBPACK_DEFAULT_EXPORT__)
});
/* ESM default export */ const __WEBPACK_DEFAULT_EXPORT__ = (2);


}),

},function(__webpack_require__) {
// webpack/runtime/get_full_hash
(() => {
__webpack_require__.h = () => ("CURRENT_HASH")
})();

}
);
