import * as DialogPrimitive from "@radix-ui/react-dialog";
import { css } from "@webstudio-is/css-engine";
import { ReactNode } from "react";
import { CrossIcon } from "../icons/cross";
import classes from "./dialog.module.css";

type DialogProps = {
  isOpen: boolean;
  onOpenChange: (isOpen: boolean) => void;
  children: ReactNode;
  title?: string;
};

export const Dialog = ({
  isOpen,
  onOpenChange,
  children,
  title,
}: DialogProps) => {
  return (
    <DialogPrimitive.Root open={isOpen} onOpenChange={onOpenChange}>
      <DialogPrimitive.Portal>
        <DialogPrimitive.Overlay className={classes.overlay} />
        <DialogPrimitive.Content className={classes.content}>
          {title && (
            <DialogPrimitive.Title className={classes.title}>
              {title}
            </DialogPrimitive.Title>
          )}
          <DialogPrimitive.Close className={classes.closeButton}>
            <CrossIcon />
          </DialogPrimitive.Close>
          {children}
        </DialogPrimitive.Content>
      </DialogPrimitive.Portal>
    </DialogPrimitive.Root>
  );
};

export const DialogTrigger = DialogPrimitive.Trigger;
export const DialogClose = DialogPrimitive.Close;

export const dialogContentStyle = css.raw(classes.content);
export const dialogOverlayStyle = css.raw(classes.overlay);
```

