let uniqueId = 0;

const renderResponsiveControls = ( viewports ) => {
	const inputId = ++uniqueId;

	return viewports.map( ( { id, label } ) => {
		return (
			<Fragment key={ `${ inputId }-${ id }` }>
				<label htmlFor={ `${ inputId }-${ id }` }>
					Custom Viewport { label }
				</label>
				<input
					id={ `${ inputId }-${ id }` }
					defaultValue={ label }
					type="range"
				/>
			</Fragment>
		);
	} );
};
```

