TITLE: Arguments Hooking (Server - TypeScript)
DESCRIPTION: Configures the WebSocket server using tgrid and @agentica/rpc to handle requests from the client. It defines the AgenticaRpcService and integrates with LlamaTypeChecker for advanced function parameter handling.
SOURCE: https://github.com/wrtnlabs/agentica/blob/main/website/content/docs/websocket/client.mdx#_snippet_5

LANGUAGE: typescript
CODE:
```
import { Agentica } from "@agentica/core";
import {
  AgenticaRpcService,
  IAgenticaRpcListener,
  IAgenticaRpcService,
} from "@agentica/rpc";
import { LlamaTypeChecker } from "@samchon/openapi";
import OpenAI from "openai";
import { WebSocketServer } from "tgrid";
import typia from "typia";

const server: WebSocketServer<
  null,
  IAgenticaRpcService<"llama">,
  IAgenticaRpcListener
> = new WebSocketServer();
await server.open(3_001, async (acceptor) => {
  const agent: Agentica<"llama"> = new Agentica({
    model: "llama",
    vendor: {
      api: new OpenAI({
        apiKey: "********",
        baseURL: "http://localhost:3000/llama",
      }),
      model: "llama-3.3-70b",
    },
    controllers: [
      {
        name: "bbs",
        protocol: "class",
        application: typia.llm.application<BbsArticleService, "llama">({
          separate: (schema) =>
            LlamaTypeChecker.isString(schema) &&
            schema.format === "uri" &&
            schema.contentMediaType !== undefined,
        }),
        execute: new BbsArticleService(),
      },
    ],
  });
  const service: AgenticaRpcService<"llama"> = new AgenticaRpcService({
    agent,
    listener: acceptor.getDriver(),
  });
  await acceptor.accept(service);
});
