  "yao.table.Insert",
  "pet",
  ["name", "type", "status", "doctor_id"],
  [
    ["Cookie", "cat", "checked", 1],
    ["Baby", "dog", "checked", 1],
    ["Poo", "others", "checked", 1],
  ]
);
