TITLE: Markdown Generator Plugin Implementation
DESCRIPTION: Main implementation of a plugin that generates markdown documentation from ZModel schema.
SOURCE: https://github.com/zenstackhq/zenstack-docs/blob/main/docs/the-complete-guide/part2/3-writing-plugins.md#2025-04-10_snippet_3

LANGUAGE: typescript
CODE:
```
import { type PluginOptions } from '@zenstackhq/sdk';
import {
    isDataModel,
    type DataModel,
    type Model,
} from '@zenstackhq/sdk/ast';
import fs from 'fs';

export const name = 'Markdown';

export default async function run(model: Model, options: PluginOptions) {
    // process options
    const title = options.title ?? 'My Application Model';
    const description = options.description ?? 'Description of my application';

    // get all data models
    const dataModels = model.declarations.filter((x): x is DataModel =>
        isDataModel(x)
    );

    // TOC
    const toc = dataModels.map((x) => `- [${x.name}](#${x.name})`).join('\n');

    // data model docs
    const dataModelDocs = generateDataModelDocs(dataModels);

    fs.writeFileSync(
        'schema.md',
        `# ${title}

${description}

${toc}

${dataModelDocs}
    `
    );
}
```

