TITLE: Post Details Page Implementation in Remix
DESCRIPTION: Component for displaying and managing a specific post. Includes functionality to view, delete, publish, and unpublish posts with error handling. Uses enhanced Prisma client to enforce access control.
SOURCE: https://github.com/zenstackhq/zenstack-docs/blob/main/docs/quick-start/remix-run.mdx#2025-04-10_snippet_11

LANGUAGE: tsx
CODE:
```
import type { ActionArgs, LoaderArgs } from '@remix-run/node';
import { json, redirect } from '@remix-run/node';
import { Form, useCatch, useLoaderData } from '@remix-run/react';
import invariant from 'tiny-invariant';
import { deletePost, getPost, publishPost, unpublishPost } from '~/models/post.server';

import { requireUserId } from '~/session.server';

export async function loader({ request, params }: LoaderArgs) {
    const userId = await requireUserId(request);
    invariant(params.postId, 'await not found');

    const post = await getPost({ userId, id: params.postId });
    if (!post) {
        throw new Response('Not Found', { status: 404 });
    }
    return json({ post });
}

export async function action({ request, params }: ActionArgs) {
    const userId = await requireUserId(request);
    invariant(params.postId, 'postId not found');

    const intent = (await request.formData()).get('intent');
    switch (intent) {
        case 'delete':
            await deletePost({ userId, id: params.postId });
            return redirect('/posts');
        case 'publish':
            await publishPost({ userId, id: params.postId });
            return null;
        case 'unpublish':
            await unpublishPost({ userId, id: params.postId });
            return null;
    }
}

export default function PostDetailsPage() {
    const data = useLoaderData<typeof loader>();

    return (
        <div>
            <h3 className="text-2xl font-bold">
                {data.post.title} {!data.post.published && <span className="text-base font-normal italic">Draft</span>}
            </h3>
            <p className="py-6">{data.post.body}</p>
            <hr className="my-4" />
            <div className="flex gap-2">
                <Form method="post">
                    <input type="hidden" name="intent" value="delete" />
                    <button
                        type="submit"
                        className="rounded bg-blue-500  py-2 px-4 text-white hover:bg-blue-600 focus:bg-blue-400"
                    >
                        Delete
                    </button>
                </Form>
                <Form method="post">
                    <input type="hidden" name="intent" value={data.post.published ? 'unpublish' : 'publish'} />
                    <button
                        type="submit"
                        className="rounded bg-blue-500  py-2 px-4 text-white hover:bg-blue-600 focus:bg-blue-400"
                    >
                        {data.post.published ? 'Unpublish' : 'Publish'}
                    </button>
                </Form>
            </div>
        </div>
    );
}

export function ErrorBoundary({ error }: { error: Error }) {
    console.error(error);

    return <div>An unexpected error occurred: {error.message}</div>;
}

export function CatchBoundary() {
    const caught = useCatch();

    if (caught.status === 404) {
        return <div>Post not found</div>;
    }

    throw new Error(`Unexpected caught response with status: ${caught.status}`);
}
```

