TITLE: Implementing Credential-Based Authentication with NextAuth
DESCRIPTION: Configuration for NextAuth using credential-based authentication with email and password, including session handling and token management.
SOURCE: https://github.com/zenstackhq/zenstack-docs/blob/main/docs/the-complete-guide/part4/1-prepare-project.md#2025-04-10_snippet_8

LANGUAGE: ts
CODE:
```
import { PrismaAdapter } from '@next-auth/prisma-adapter';
import type { PrismaClient } from '@prisma/client';
import { compare } from 'bcryptjs';
import NextAuth, { type DefaultSession, type NextAuthOptions } from 'next-auth';
import CredentialsProvider from 'next-auth/providers/credentials';
import { db } from './db';

declare module 'next-auth' {
  interface Session extends DefaultSession {
    user: {
      id: string;
    } & DefaultSession['user'];
  }
}

export const authOptions: NextAuthOptions = {
  session: {
    strategy: 'jwt',
  },
  // Include user.id on session
  callbacks: {
    session({ session, token }) {
      if (session.user) {
        session.user.id = token.sub!;
      }
      return session;
    },
  },
  // Configure one or more authentication providers
  adapter: PrismaAdapter(db),
  providers: [
    CredentialsProvider({
      credentials: {
        email: { type: 'email' },
        password: { type: 'password' },
      },
      authorize: authorize(db),
    }),
  ],
};

function authorize(prisma: PrismaClient) {
  return async (credentials: Record<'email' | 'password', string> | undefined) => {
    if (!credentials?.email) throw new Error('"email" is required in credentials');
    if (!credentials?.password) throw new Error('"password" is required in credentials');

    const maybeUser = await prisma.user.findFirst({
      where: { email: credentials.email },
      select: { id: true, email: true, password: true },
    });
    if (!maybeUser?.password) return null;

    // verify the input password with stored hash
    const isValid = await compare(credentials.password, maybeUser.password);
    if (!isValid) return null;

    return { id: maybeUser.id, email: maybeUser.email };
  };
}

export default NextAuth(authOptions);
```

