TITLE: New Post Creation Page Component in React
DESCRIPTION: React component implementation for the new post creation form with validation and error handling using Remix form helpers.
SOURCE: https://github.com/zenstackhq/zenstack-docs/blob/main/versioned_docs/version-1.x/quick-start/remix-run.mdx#2025-04-10_snippet_9

LANGUAGE: tsx
CODE:
```
import type { ActionArgs } from '@remix-run/node';
import { json, redirect } from '@remix-run/node';
import { Form, useActionData } from '@remix-run/react';
import * as React from 'react';
import { createPost } from '~/models/post.server';

import { requireUserId } from '~/session.server';

export async function action({ request }: ActionArgs) {
    const userId = await requireUserId(request);

    const formData = await request.formData();
    const title = formData.get('title');
    const body = formData.get('body');

    if (typeof title !== 'string' || title.length === 0) {
        return json({ errors: { title: 'Title is required', body: null } }, { status: 400 });
    }

    if (typeof body !== 'string' || body.length === 0) {
        return json({ errors: { title: null, body: 'Body is required' } }, { status: 400 });
    }

    const post = await createPost({ title, body, userId });

    return redirect(`/posts/${post.id}`);
}

export default function NewPostPage() {
    const actionData = useActionData<typeof action>();
    const titleRef = React.useRef<HTMLInputElement>(null);
    const bodyRef = React.useRef<HTMLTextAreaElement>(null);

    React.useEffect(() => {
        if (actionData?.errors?.title) {
            titleRef.current?.focus();
        } else if (actionData?.errors?.body) {
            bodyRef.current?.focus();
        }
    }, [actionData]);

    return (
        <Form
            method="post"
            style={{
                display: 'flex',
                flexDirection: 'column',
                gap: 8,
                width: '100%',
            }}
        >
            <div>
                <label className="flex w-full flex-col gap-1">
                    <span>Title: </span>
                    <input
                        ref={titleRef}
                        name="title"
                        className="flex-1 rounded-md border-2 border-blue-500 px-3 text-lg leading-loose"
                        aria-invalid={actionData?.errors?.title ? true : undefined}
                        aria-errormessage={actionData?.errors?.title ? 'title-error' : undefined}
                    />
                </label>
                {actionData?.errors?.title && (
                    <div className="pt-1 text-red-700" id="title-error">
                        {actionData.errors.title}
                    </div>
                )}
            </div>

            <div>
                <label className="flex w-full flex-col gap-1">
                    <span>Body: </span>
                    <textarea
                        ref={bodyRef}
                        name="body"
                        rows={8}
                        className="w-full flex-1 rounded-md border-2 border-blue-500 py-2 px-3 text-lg leading-6"
                        aria-invalid={actionData?.errors?.body ? true : undefined}
                        aria-errormessage={actionData?.errors?.body ? 'body-error' : undefined}
                    />
                </label>
                {actionData?.errors?.body && (
                    <div className="pt-1 text-red-700" id="body-error">
                        {actionData.errors.body}
                    </div>
                )}
            </div>

            <div className="text-right">
                <button
                    type="submit"
                    className="rounded bg-blue-500 py-2 px-4 text-white hover:bg-blue-600 focus:bg-blue-400"
                >
                    Save
                </button>
            </div>
        </Form>
    );
}
```

