TITLE: Implementing Next.js Signup Page with ZenStack
DESCRIPTION: TypeScript React component for user signup functionality. Uses ZenStack's useMutateUser hook for user creation and NextAuth for session management. Includes form validation and error handling for duplicate users.
SOURCE: https://github.com/zenstackhq/zenstack-docs/blob/main/versioned_docs/version-1.x/quick-start/nextjs.mdx#2025-04-10_snippet_10

LANGUAGE: tsx
CODE:
```
/* eslint-disable @typescript-eslint/no-explicit-any */
/* eslint-disable @typescript-eslint/no-unsafe-member-access */
import type { NextPage } from 'next';
import { signIn } from 'next-auth/react';
import Router from 'next/router';
import { useState, type FormEvent } from 'react';
import { useMutateUser } from '../lib/hooks';

const Signup: NextPage = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const { createUser: signup } = useMutateUser();

    async function onSignup(e: FormEvent) {
        e.preventDefault();
        try {
            await signup({ data: { email, password } });
        } catch (err: any) {
            console.error(err);
            if (err.info?.prisma && err.info?.code === 'P2002') {
                alert('User alread exists');
            } else {
                alert('An unknown error occurred');
            }
            return;
        }

        await signIn('credentials', { redirect: false, email, password });
        await Router.push('/');
    }

    return (
        <div className="flex min-h-screen flex-col items-center justify-center bg-gradient-to-b from-[#2e026d] to-[#15162c]">
            <h1 className="text-5xl font-extrabold text-white">Sign up</h1>
            <form className="mt-16 flex flex-col gap-8 text-2xl" onSubmit={(e) => void onSignup(e)}>
                <div>
                    <label htmlFor="email" className="inline-block w-32 text-white">
                        Email
                    </label>
                    <input
                        id="email"
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.currentTarget.value)}
                        className="ml-4 w-72 rounded border p-2"
                    />
                </div>
                <div>
                    <label htmlFor="password" className="inline-block w-32 text-white ">
                        Password
                    </label>
                    <input
                        id="password"
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.currentTarget.value)}
                        className="ml-4 w-72 rounded border p-2"
                    />
                </div>
                <input
                    type="submit"
                    value="Create account"
                    className="cursor-pointer rounded border border-gray-500 py-4 text-white"
                />
            </form>
        </div>
    );
};

export default Signup;
```

