# Get Isolation Profile ID
data "zpa_isolation_profile" "this" {
    name = "zpa_isolation_profile"
}
# Retrieve Identity Provider ID
data "zpa_idp_controller" "this" {
	name = "Idp_Name"
}

# Retrieve SAML Attribute ID
